#include <bits/stdc++.h>using namespace std;#define int long longconst double pi = 3.14159265359;signed main() {  ios::sync_with_stdio(false);  cin.tie(nullptr);  double a,b,h,m;  cin >> a >> b >> h >> m;  double mAngle = 6 * m;  double hAngle = 30 * h + 0.5 * m;  double Angle = abs(mAngle - hAngle);  if(Angle > 180) {    Angle = 360 - Angle;  }  Angle = Angle * pi / 180.0;  double ans = (a * a) +(b * b) - 2 * a * b * cos(Angle);  ans = sqrt(ans);  cout << fixed << setprecision(10) << ans << '\n';}